document.addEventListener('DOMContentLoaded', function () {
    const closeButton = document.querySelector('.pum-close'); // Selector for the popup close button

    if (closeButton) {
        closeButton.addEventListener('click', function (event) {
            event.preventDefault(); // Prevent any default close actions
            window.history.go(-1); // Go back to the previous page
        });
    }
});
